module.exports = function (RED) {
	var CryptoJS = require("crypto-js");

	function DigestNode(config) {
		RED.nodes.createNode(this, config);

		var node = this;
		node.algorithm = config.algorithm;
		node.key = config.key;
		node.value = config.value;

		node.on('input', function (msg) {
			var key = RED.util.getMessageProperty(msg,node.key);
			var value = RED.util.getMessageProperty(msg,node.value);
			// check configurations
			if(!node.algorithm || !node.key || !node.value) {
				// rising misconfiguration error
				node.error("Missing configuration, please check your algorithm or key or value.", msg);
			} else {
				// check the payload
				if(key && value) {
					// debugging message
					node.debug('Creating a digest of payload using '+node.algorithm);
					// digest with CryptoJS
					msg.payload = CryptoJS[node.algorithm](value, key).toString();
				} else {
					// debugging message
					node.trace('Nothing to digest: empty payload');
				}

				node.send(msg);
			}
		});
	}

	RED.nodes.registerType("hmac", DigestNode);
};